mapper.insert:
INSERT
	ALL
INTO	
	company 
		values(
		#{comp_seqs}
		, #{comp_id}
		, #{comp_name}
		, #{comp_tel}
		, #{comp_address}
		, #{comp_address2}
		)
INTO
	employee 
		values(
		emp_seqs.nextval
		,#{comp_seqs}
		,#{emp_id}
		,#{emp_pw}
		,#{emp_auth_level}
		,#{emp_name}
		,#{emp_tel}
		,#{emp_quiz}
		,#{emp_quiz_answer}
		)
SELECT
	 *
FROM
	 DUAL

mapper.update:
UPDATE
	company
SET
	comp_name = #{comp_name}
	, comp_tel = #{comp_tel}
	, comp_address = #{comp_address}
	, comp_address2 = #{comp_address2}
WHERE
	comp_seq=#{comp_seq}

UPDATE
	employee
SET
	emp_pw=#{emp_pw}
	,emp_name=#{emp_name}
	,emp_tel=#{emp_tel}
WHERE
	comp_seq=#{comp_seq} and emp_id=#{emp_id}
