mapper.insert:
    INSERT INTO 
    	seat (
	    seat_seq
	    , comp_seq
	    , seat_id
	    ) VALUES (
	    seat_seqs.nextval
	    , #{comp_seq}
	    , #{seat_id}
	    )

mapper.update:
UPDATE
	SEAT
SET
	seat_id = #{seat_id}
WHERE
	seat_seq = #{seat_seq}

mapper.insert:
    INSERT INTO 
    	MENU 
    	(
	    menu_seq,
	    , comp_seq
	    , menu_sellFlag
	    , menu_name
	    , menu_price
	    , menu_category
	    )
    	VALUES (
	    menu_seqs.nextval
	    , #{comp_seq}
	    , #{menu_sellFlag}
	    , #{menu_name}
	    , #{menu_price}
	    , #{menu_category}
	    )

mapper.delete:
DELETE FROM 
	MENU
WHERE 
	menu_seq = #{menu_seq} 
	and comp_seq = #{comp_seq}

mapper.update:
    UPDATE
    	MENU
    SET
	    menu_sellFlag = #{menu_sellFlag}
	    , menu_name = #{menu_name}
	    , menu_price = #{menu_price}
	    , menu_category = #{menu_category}
    WHERE
    	menu_seq = #{menu_seq} 
    	and comp_seq = #{comp_seq}

mapper.insert:
    INSERT INTO 
    	EXPENSE
    	values(
	    expense_seqs.nextval
	    , #{comp_seq}
	    , #{expense_type}
	    , to_date(#{expense_date}, 'YYYY-MM-DD')
	    , #{expense_description}
	    , #{expense_amount}
	    )

mapper.update:
    UPDATE
    	EXPENSE
    SET
	    expense_date = #{expense_date}
	    , expense_type = #{expense_type}
	    , expense_description = #{expense_description}
	    , expense_amount = #{expense_amount}
    WHERE
    	expense_seq = #{expense_seq}
